package leetcode;

public class A402_Remove_K_Digits {
    public String removeKdigits(String num, int k) {
        if (num.length() <= k) return "0";
        if (k <= 0) return num;
        
        //handle case like 12300456 k >= 3
        int index0 = num.indexOf('0');
        if (index0 >= 0 && k >= index0) {
        	int j = index0 + 1;
        	while (j < num.length() && num.charAt(j) == '0') j++;
        	return removeKdigits(num.substring(j), k - index0);
        }
        
        StringBuilder sb = new StringBuilder();
        
        // remove any decreasing sequence
        // algorithm: if num[i] <= num[i - 1], remove num[i]
        for (int p = 0; p < num.length(); p++) {
        	char c = num.charAt(p);
        	sb.append(c);
        	while (sb.length() > 1 && c < sb.charAt(sb.length() - 2)) {
        		sb.deleteCharAt(sb.length() - 2);
        		k--;
        		if (k <= 0) {
        			sb.append(num.substring(p + 1));
        			p = num.length();
        			break;
        		}
        	}
        	//System.out.println(sb);
        }
        
        // numbers left are in increasing order
        // remove last k
        if (k > 0) {
        	sb.delete(sb.length() - k, sb.length());
        }
        
        return sb.toString();
        
    }
    public String removeKdigits2(String num, int k) {
        if (k >= num.length()) return "0";
        
        StringBuilder sb = new StringBuilder();
        sb.append(num.charAt(0));
        
        int i = 1;
        while (k > 0) {
            int curr = i >= num.length() ? -1 : Integer.parseInt(num.charAt(i) + "");
            if (sb.length() == 0) {
                sb.append(curr);
                i++;
            }
            else {
                if (Integer.parseInt(sb.charAt(sb.length() - 1) + "") > curr) {
                    sb.deleteCharAt(sb.length() - 1);
                    k--;
                }
                else {
                    sb.append(curr);
                    i++;
                }
            }
        }
        
        if (i < num.length()) {
            sb.append(num.substring(i, num.length()));
        }
        
        while (sb.length() > 0 && sb.charAt(0) == '0') {
            sb.deleteCharAt(0);
        }
        
        if (sb.length() > 0) {
            return sb.toString();
        }
        else {
            return "0";
        }
    }
    
	public static void main(String[] args) {
		A402_Remove_K_Digits rk = new A402_Remove_K_Digits();
		String num1 = "1432219";
		int k1 = 3;
		String num2 = "10200";
		int k2 = 1;
		String num3 = "10";
		int k3 = 2;
		String num4 = "12";
		int k4 = 1;
		String num5 = "21";
		int k5 = 1;
		System.out.println(rk.removeKdigits(num1, k1));
		System.out.println(rk.removeKdigits(num2, k2));
		System.out.println(rk.removeKdigits(num3, k3));
		System.out.println(rk.removeKdigits(num4, k4));
		System.out.println(rk.removeKdigits(num5, k5));
		String num6 = "";
		int k6 = 555;
		System.out.println(rk.removeKdigits(num6, k6));
	}

}
